// frontend/src/components/Navigation.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const Navigation = () => {
  const { isAuthenticated, isAdmin, logout } = useAuth();
  const navigate = useNavigate();

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
      <div className="container">
        <Link className="navbar-brand" to="/">Perpustakaan</Link>

        {isAuthenticated && (
          <div className="collapse navbar-collapse">
            <ul className="navbar-nav me-auto">
              <li className="nav-item">
                <Link className="nav-link" to="/buku">Buku</Link>
              </li>
              {/* Only show Mahasiswa link to admin */}
              {isAdmin && (
                <li className="nav-item">
                  <Link className="nav-link" to="/mahasiswa">Mahasiswa</Link>
                </li>
              )}
              <li className="nav-item">
                <Link className="nav-link" to="/peminjaman">Peminjaman</Link>
              </li>
            </ul>
            <button 
              className="btn btn-outline-light" 
              onClick={() => {
                logout();
                navigate('/login');
              }}
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navigation;
