import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { ApolloProvider } from '@apollo/client';
import client from './apolloClient';
import { AuthProvider, useAuth } from './components/AuthContext';

import LoginPage from './components/LoginPage';
// import Dashboard from './components/Dashboard';
import BukuPage from './components/BukuPage';
import MahasiswaPage from './components/MahasiswaPage';
import PeminjamanPage from './components/PeminjamanPage';
import Navigation from './components/Navigation';

// Protected route wrapper
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();
  return isAuthenticated ? children : <Navigate to="/login" />;
};

function App() {
  return (
    <ApolloProvider client={client}>
      <AuthProvider>
        <BrowserRouter>
          <Navigation />
          <Routes>
            <Route path="/login" element={<LoginPage />} />

            {/* <Route path="/dashboard" element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            } /> */}

            <Route path="/buku" element={<BukuPage />} />

            <Route path="/mahasiswa" element={
              <ProtectedRoute>
                <MahasiswaPage />
              </ProtectedRoute>
            } />

            <Route path="/peminjaman" element={
              <ProtectedRoute>
                <PeminjamanPage />
              </ProtectedRoute>
            } />

            <Route path="/" element={<Navigate to="/login" />} />
          </Routes>
        </BrowserRouter>
      </AuthProvider>
    </ApolloProvider>
  );
}

export default App;
