import React, { useState } from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { GET_ALL_BOOKS } from '../graphql/bukuQueries';
import { CREATE_BOOK, UPDATE_BOOK, DELETE_BOOK } from '../graphql/bukuMutations';
import { useAuth } from './AuthContext';

const Buku = () => {
  const { isAuthenticated } = useAuth();
  const [formData, setFormData] = useState({
    isbn: '',
    judul: '',
    pengarang: '',
    penerbit: '',
    tahun_terbit: '',
    jumlah: ''
  });
  const [editMode, setEditMode] = useState(false);
  const [currentId, setCurrentId] = useState(null);

  // Query dan mutations...
  const { loading, error, data, refetch } = useQuery(GET_ALL_BOOKS);

  // Handlers untuk form dan CRUD operations...

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="container">
      <h2>Manajemen Buku</h2>

      {isAuthenticated && (
        <form onSubmit={handleSubmit}>
          {/* Form fields */}
        </form>
      )}

      <table className="table">
        <thead>
          <tr>
            <th>ISBN</th>
            <th>Judul</th>
            <th>Pengarang</th>
            <th>Penerbit</th>
            <th>Tahun</th>
            <th>Jumlah</th>
            {isAuthenticated && <th>Aksi</th>}
          </tr>
        </thead>
        <tbody>
          {data?.getAllBuku?.map(book => (
            <tr key={book.id}>
              <td>{book.isbn}</td>
              <td>{book.judul}</td>
              <td>{book.pengarang}</td>
              <td>{book.penerbit}</td>
              <td>{book.tahun_terbit}</td>
              <td>{book.jumlah}</td>
              {isAuthenticated && (
                <td>
                  <button 
                    className="btn btn-sm btn-warning me-2"
                    onClick={() => handleEdit(book)}
                  >
                    Edit
                  </button>
                  <button 
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(book.id)}
                  >
                    Hapus
                  </button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Buku;
