import { gql } from '@apollo/client';

export const CREATE_PEMINJAMAN = gql`
  mutation CreatePeminjaman($mahasiswa_id: ID!, $buku_id: ID!) {
    createPeminjaman(mahasiswa_id: $mahasiswa_id, buku_id: $buku_id) {
      id
      mahasiswa_id
      buku_id
      tanggal_pinjam
      status
    }
  }
`;

export const UPDATE_PEMINJAMAN_STATUS = gql`
  mutation UpdatePeminjamanStatus($id: ID!, $status: String!) {
    updatePeminjamanStatus(id: $id, status: $status) {
      id
      status
      tanggal_kembali
    }
  }
`;
