import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useMutation, gql } from '@apollo/client';
import { useAuth } from './AuthContext';

const LOGIN_MUTATION = gql`
  mutation Login($nim: String!, $email: String!) {
    login(nim: $nim, email: $email) {
      token
      user {
        id
        nim
        nama
      }
    }
  }
`;

const LoginPage = () => {
  const [formData, setFormData] = useState({
    nim: '',
    email: ''
  });
  const [error, setError] = useState(null);

  const [loginMutation, { loading }] = useMutation(LOGIN_MUTATION);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      const { data } = await loginMutation({ 
        variables: { 
          nim: formData.nim, 
          email: formData.email 
        } 
      });

      if (data?.login) {
        // Simpan token dan data user
        login({ 
          token: data.login.token, 
          role: data.login.user.role || 'MAHASISWA' // Asumsi default role
        });
        navigate('/dashboard');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError(err.message || 'NIM atau Email salah');
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center mt-5">
        <div className="col-md-6">
          <div className="card shadow">
            <div className="card-header bg-primary text-white text-center">
              <h3 className="my-2">Login</h3>
            </div>
            <div className="card-body p-4">
              {error && (
                <div className="alert alert-danger" role="alert">
                  {error}
                </div>
              )}

              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="nim" className="form-label">NIM</label>
                  <input
                    type="text"
                    className="form-control"
                    id="nim"
                    name="nim"
                    value={formData.nim}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="d-grid">
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={loading}
                  >
                    {loading ? 'Loading...' : 'Login'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
