import React, { useState } from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { CREATE_BOOK, UPDATE_BOOK, DELETE_BOOK } from '../graphql/bukuMutations';
import { GET_ALL_BOOKS, GET_BOOKS_BY_JUDUL } from '../graphql/bukuQueries';import { bukuClient } from '../apollo/clients';
import { useAuth } from './components/AuthContext';

const Buku = () => {
  const { canAccess } = useAuth();
  const [formData, setFormData] = useState({/* state form */});
  const [editMode, setEditMode] = useState(false);
  const [currentId, setCurrentId] = useState(null);

  // Gunakan bukuClient khusus untuk query buku
  const { loading, error, data, refetch } = useQuery(GET_ALL_BOOKS, { 
    client: bukuClient 
  });

  const [createBook] = useMutation(CREATE_BOOK, {
    client: bukuClient,
    onCompleted: () => refetch()
  });

  const [updateBook] = useMutation(UPDATE_BOOK, {
    client: bukuClient,
    onCompleted: () => refetch()
  });

  const [deleteBook] = useMutation(DELETE_BOOK, {
    client: bukuClient,
    onCompleted: () => refetch()
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editMode) {
        await updateBook({
          variables: {
            id: currentId,
            input: formData
          }
        });
      } else {
        await createBook({
          variables: {
            input: formData
          }
        });
      }
      // Reset form
      setFormData({/* reset form */});
      setEditMode(false);
    } catch (err) {
      console.error('Error saving book:', err);
    }
  };

  // Implementasi handleDelete, handleEdit, dll.

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="container">
      <h2>Manajemen Buku</h2>

      {/* Form tambah/edit buku */}
      {canAccess('buku', 'create') && (
        <form onSubmit={handleSubmit}>
          {/* Form fields */}
          <button type="submit">
            {editMode ? 'Update Buku' : 'Tambah Buku'}
          </button>
        </form>
      )}

      {/* Tabel daftar buku */}
      <table className="table">
        <thead>
          <tr>
            <th>ISBN</th>
            <th>Judul</th>
            <th>Pengarang</th>
            <th>Penerbit</th>
            <th>Tahun</th>
            <th>Jumlah</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {data?.getAllBooks.map(book => (
            <tr key={book.id}>
              <td>{book.isbn}</td>
              <td>{book.judul}</td>
              <td>{book.pengarang}</td>
              <td>{book.penerbit}</td>
              <td>{book.tahun_terbit}</td>
              <td>{book.jumlah}</td>
              <td>
                {/* Aksi */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Buku;
