import React, { useEffect, useState } from 'react';
import { apiFetch } from '../api/gateway';

export default function PeminjamanPage() {
  const [peminjaman, setPeminjaman] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    apiFetch('peminjaman/graphql', {
      method: 'POST',
      body: JSON.stringify({ query: '{ getAllPeminjaman { id mahasiswa_id buku_id tanggal_pinjam tanggal_kembali status } }' }),
    })
      .then(data => setPeminjaman(data.data.getAllPeminjaman))
      .catch(e => alert(e.message))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div>Loading...</div>;
  return (
    <div>
      <h2>Daftar Peminjaman</h2>
      <ul>
        {peminjaman.map(p => (
          <li key={p.id}>
            Mahasiswa: {p.mahasiswa_id}, Buku: {p.buku_id}, Pinjam: {p.tanggal_pinjam}, Kembali: {p.tanggal_kembali || '-'}, Status: {p.status}
          </li>
        ))}
      </ul>
    </div>
  );
}
