import React, { useState } from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { GET_ALL_BOOKS } from '../graphql/bukuQueries';
import { CREATE_BOOK, UPDATE_BOOK, DELETE_BOOK } from '../graphql/bukuMutations';
import { useAuth } from '../components/AuthContext';

const Buku = () => {
  const { role } = useAuth();
  const [formData, setFormData] = useState({/* state form */});
  const [editMode, setEditMode] = useState(false);
  const [currentId, setCurrentId] = useState(null);

  const { loading, error, data, refetch } = useQuery(GET_ALL_BOOKS);

  const [createBook] = useMutation(CREATE_BOOK, {
    onCompleted: () => refetch()
  });

  const [updateBook] = useMutation(UPDATE_BOOK, {
    onCompleted: () => refetch()
  });

  const [deleteBook] = useMutation(DELETE_BOOK, {
    onCompleted: () => refetch()
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editMode) {
        await updateBook({
          variables: {
            id: currentId,
            input: formData
          }
        });
      } else {
        await createBook({
          variables: {
            input: formData
          }
        });
      }
      // Reset form
      setFormData({/* reset form */});
      setEditMode(false);
    } catch (err) {
      console.error('Error saving book:', err);
    }
  };

  // Implementasi handleDelete, handleEdit, dll.

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="container">
      <h2>Manajemen Buku</h2>

      {/* Tampilkan form hanya untuk ADMIN */}
      {role === 'ADMIN' && (
        <form onSubmit={handleSubmit}>
          {/* Form fields */}
          <button type="submit">
            {editMode ? 'Update Buku' : 'Tambah Buku'}
          </button>
        </form>
      )}

      {/* Tampilkan tombol edit/hapus berdasarkan role */}
      <table className="table">
        <thead>
          <tr>
            <th>ISBN</th>
            <th>Judul</th>
            <th>Pengarang</th>
            <th>Penerbit</th>
            <th>Tahun</th>
            <th>Jumlah</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {data?.getAllBuku?.map(book => (
            <tr key={book.id}>
              <td>{book.isbn}</td>
              <td>{book.judul}</td>
              <td>{book.pengarang}</td>
              <td>{book.penerbit}</td>
              <td>{book.tahun_terbit}</td>
              <td>{book.jumlah}</td>
              <td>
                {/* Tombol edit hanya untuk ADMIN dan PUSTAKAWAN */}
                {(role === 'ADMIN' || role === 'PUSTAKAWAN') && (
                  <button 
                    className="btn btn-sm btn-warning me-2"
                    onClick={() => handleEdit(book)}
                  >
                    Edit
                  </button>
                )}

                {/* Tombol hapus hanya untuk ADMIN */}
                {role === 'ADMIN' && (
                  <button 
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(book.id)}
                  >
                    Hapus
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Buku;
