import React, { useEffect, useState } from 'react';
import { apiFetch } from '../api/gateway';

export default function MahasiswaPage() {
  const [mahasiswa, setMahasiswa] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    apiFetch('mahasiswa/graphql', {
      method: 'POST',
      body: JSON.stringify({ query: '{ getAllMahasiswa { id nim nama jurusan angkatan email } }' }),
    })
      .then(data => setMahasiswa(data.data.getAllMahasiswa))
      .catch(e => alert(e.message))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div>Loading...</div>;
  return (
    <div>
      <h2>Daftar Mahasiswa</h2>
      <ul>
        {mahasiswa.map(m => (
          <li key={m.id}>
            {m.nim} - {m.nama} ({m.jurusan}, {m.angkatan}) - {m.email}
          </li>
        ))}
      </ul>
    </div>
  );
}
