import React, { useState } from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { GET_ALL_PEMINJAMAN } from '../graphql/peminjamanQueries';
import { CREATE_PEMINJAMAN, UPDATE_PEMINJAMAN_STATUS } from '../graphql/peminjamanMutations';
import { peminjamanClient } from '../apolloClients'; // Gunakan peminjamanClient (4003)
import { GET_ALL_BOOKS } from '../graphql/bukuQueries';
import { bukuClient } from '../apolloClients'; // Gunakan bukuClient untuk data buku

const PeminjamanPage = () => {
  // Query data peminjaman dari port 4003
  const { data: peminjamanData, loading: loadingPeminjaman, refetch } = useQuery(
    GET_ALL_PEMINJAMAN, 
    { client: peminjamanClient }
  );

  // Query data buku dari port 4002
  const { data: bukuData } = useQuery(
    GET_ALL_BOOKS,
    { client: bukuClient }
  );

  // Mutation untuk membuat peminjaman menggunakan port 4003
  const [createPeminjaman] = useMutation(CREATE_PEMINJAMAN, {
    client: peminjamanClient,
    onCompleted: () => refetch()
  });

  // Definisikan handlers dan render UI...
};
